# SUMMARY
# =============================================================================
Write-Host ""
Write-Host "=======================================" -ForegroundColor White
Write-Host "         BUILD COMPLETE v$version" -ForegroundColor White
Write-Host "=======================================" -ForegroundColor White
Write-Host ""
Write-Host "  $outputExeName" -ForegroundColor Green
Write-Host "  Size: $([Math]::Round($size, 2)) MB" -ForegroundColor Gray
Write-Host ""



